def merge_the_tools(string, k):
    lista = []
    part = ""
    counter = 0
    for i in range(len(string)):
        if (counter<k):
            part += string[i]
            counter +=1
        else:
            lista.append(part)
            part = string[i]
            counter = 1
    lista.append(part)

    print(lista)
    
    for i in lista:
        char = []
        for j in i:
            if j not in char:
                char.append(j)
        print(''.join(char))

